import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { INVALID_CREDENTIALS } from 'src/app/domain/constants';
import { AuthService } from '../../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {
  loginForm: FormGroup;
  emptyInputs: boolean = false;
  invalidCredentials = false;

  constructor(private readonly authService: AuthService) {
    this.loginForm = this.createLoginForm();
   }

  ngOnInit(): void {
  }

  private createLoginForm() {
    return new FormGroup({
      login: new FormControl(''),
      password: new FormControl('')
    })
  }

  async login() {    const login = this.loginForm.get('login')?.value;
    const password = this.loginForm.get('password')?.value;

    if(login && password && login !== '' && password !== '') {
      const result = await this.authService.login({login: login, password: password})
      if(result === INVALID_CREDENTIALS) this.invalidCredentials = false;
    }

    else {
      this.emptyInputs = true;
      this.invalidCredentials = false;
    } 
  }

}
