import { Injectable } from '@angular/core';
import axios, { AxiosInstance } from 'axios';
import { BACKEND_APP_URL } from '../domain/constants';
import { AuthLoginRequest } from './dtos/auth-login-request.dto';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  private readonly httpClient: AxiosInstance

  constructor() {
    this.httpClient = axios.create({
      baseURL: `${BACKEND_APP_URL}/auth`
    })
  }

  async login(req: AuthLoginRequest) {
    const data = await this.httpClient.post('/login', {login: req.login, password: req.password})
  }
}
